import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [count, setState] = useState(0)

  const increase = () => {
    setState(1)
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>{count}</h2>
        <button onClick={() => increase}>Click</button>
      </header>
    </div>
  );
}

export default App;
