import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import Login from './components/Login';
import Button from './components/Button';
import Train from './components/Train';
import { Dispatch } from 'redux';
import { shallowEqual, useDispatch, useSelector } from 'react-redux';
import { increaseCountAction } from './store/counter/action';
import { ICounterState } from './store/counter/reducer';

function App() {
  // const [count, setState] = useState(0)
  const dispatch: Dispatch<any> = useDispatch()
  const countState = useSelector(
    (state: ICounterState) => state.counter,
    shallowEqual
  )

  const increase = () => {
    // setState(1)
    // setState((pre) => pre+1)
    dispatch(increaseCountAction(1))
  }

  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        <h2>{countState}</h2>
        <button onClick={increase}>Click</button>
        {/* <Button name='on change' type={1} onHandler={increase}/>
        <Login /> */}
        {/* <Train/> */}
      </header>
    </div>
  );
}

export default App;
